mapper.update:
UPDATE feed
SET view_count = view_count + 1
WHERE feed_id = #{feedId}

UPDATE feed


        district_id = #{districtId},


        title = #{title},


        cafe_name = #{cafeName},


        content = #{content}


WHERE feed_id = #{feedId} and user_id = #{userId}

mapper.select:
SELECT user_id
FROM feed
WHERE feed_id = #{feedId}
